INSERT INTO locations (creator_id, map_id, title, description, image_url, lat, lng, created_date) VALUES (4, 4, 'Sundance Canyon Trail', 'This route leaves from the heritage Cave and Basin National Historic Site and is the ideal add-on for those seeking a hike with some cultural flair. Explore surrounding boardwalks and take in what the historic site has to offer before embarking on the calm, paved path through the valley leading to Sundance Canyon. Climb through the canyon for rushing water and smooth rock textures before returning the way you came.', 'https://d3owbckoeihl9o.cloudfront.net/images/10adv/wp-content/uploads/2021/03/Banff-Sundance-Canyon-1709-633-1295x480.jpg', 51.1528530668099, -115.61232065721617, '2021-12-17');
INSERT INTO locations (creator_id, map_id, title, description, image_url, lat, lng, created_date) VALUES (4, 4, 'Norquay Road', 'This one seems intense... "The bike ride up the Norquay Scenic Road is a wonderful route, providing great views on a challenging road made up of repetitive switchbacks. This is a great training ride and also a nice ride to pair with one of Banff''s other bike rides."', 'https://www.dangerousroads.org/images/stories/__Roads00000cf/Mount%20Norquay0.jpg', 51.19103277958444, -115.58789970142733, '2022-04-24');
INSERT INTO locations (creator_id, map_id, title, description, image_url, lat, lng, created_date) VALUES (4, 4, 'Hoodoos Bike Trail', '"The moderate Hoodoos Trail Bike Loop is a great way to create a full circuit from town by linking trails and roads. Cut out the shuttle and ride the rolling Hoodoos Trail right from your accommodation."', 'https://d3owbckoeihl9o.cloudfront.net/images/10adv/wp-content/uploads/2021/03/CA-AB-BANFF-NATIONAL-PARK-HOODOOS-TRAIL-01-2560x1920-Gallery-View-from-red-chairs-Banff-Hoodoos-trail.jpg', 51.17141773338273, -115.54977107259235, '2022-02-04');
